-- 1. ARTS & SPORTS SCIENCE > SPORTS
-- Combinations: AS2009, AS2006, AS2011, AS2027...
WITH track AS (SELECT id, pathway_id FROM tracks WHERE code = 'SPORTS'),
     pathway AS (SELECT id FROM pathways WHERE code = 'ARTS')
ON CONFLICT (code) DO NOTHING;

-- 2. ARTS & SPORTS SCIENCE > ARTS
WITH track AS (SELECT id FROM tracks WHERE code = 'ARTS_T')
ON CONFLICT (code) DO NOTHING;

-- 3. SOCIAL SCIENCES > LANGUAGES & LITERATURE
WITH track AS (SELECT id FROM tracks WHERE code = 'LANG_LIT')
ON CONFLICT (code) DO NOTHING;

-- 4. STEM > APPLIED SCIENCES
WITH track AS (SELECT id FROM tracks WHERE code = 'APP_SCI')
ON CONFLICT (code) DO NOTHING;

-- 5. STEM > PURE SCIENCES
WITH track AS (SELECT id FROM tracks WHERE code = 'PURE_SCI')
ON CONFLICT (code) DO NOTHING;

-- 6. STEM > TECHNICAL STUDIES
WITH track AS (SELECT id FROM tracks WHERE code = 'TECH')
ON CONFLICT (code) DO NOTHING;

-- 7. SOCIAL SCIENCES > HUMANITIES
WITH track AS (SELECT id FROM tracks WHERE code = 'HUM_BUS')
ON CONFLICT (code) DO NOTHING;
